
insert into films (genre, name, plot, premier_in_good_quality, rf_premier, world_premier, year, youtube_id, path)
values ('Detective','Интерстеллар', 'Когда засуха приводит человечество к продовольственному кризису, коллектив исследователей и учёных отправляется сквозь червоточину (которая предположительно соединяет области пространства-времени через большое расстояние) в путешествие, чтобы превзойти прежние ограничения для космических путешествий человека и переселить человечество на другую планету.',CURRENT_DATE , CURRENT_DATE , CURRENT_DATE , 2016, 'N0mAYAGBy_g','1.jpg'),
('Comedy','Громкая связь','Семеро друзей, собравшись в загородном доме, в шутку начинают игру — участники должны зачитывать вслух все приходящие им сообщения, а на звонки отвечать только по громкой связи. Они и представить себе не могут, какие удивительные откровения им предстоит сделать друг о друге. ',CURRENT_DATE , CURRENT_DATE , CURRENT_DATE , 2010,'LXBkjw0wojk','2.jpg'),
('Action','Меч короля Артура','Молодой Артур живёт на задворках Лондиниума вместе со своей бандой. Он и понятия не имел о своём королевском происхождении, пока однажды не взял в руки меч Экскалибур. Меч начинает менять Артура. В результате он присоединяется к сопротивлению и таинственной молодой девушке по имени Гвиневра. Ему предстоит научиться понимать магическое оружие, которым он овладел, столкнуться со своими собственными демонами и объединить народ в борьбе против диктатора Вортигерна, убившего его родителей и завладевшего короной.',CURRENT_DATE , CURRENT_DATE , CURRENT_DATE , 2018,'KCgtQUb03GA','3.jpg'),
('Drama','Зелёная книга','Утонченный светский лев, богатый и талантливый музыкант нанимает в качестве водителя и телохранителя человека, который менее всего подходит для этой работы. Тони «Болтун» — вышибала, не умеющий держать рот на замке и пользоваться столовыми приборами, зато он хорошо работает кулаками. Это турне навсегда изменит жизнь обоих.',CURRENT_DATE , CURRENT_DATE , CURRENT_DATE , 2019,'e6b9urtUJt0','4.jpg'),
('Action','Алита: боевой ангел','Планета Земля, XXVI век. Доктор Идо в груде мусора находит девушку-киборга и восстанавливает ее. Вместе с телом к ней возвращаются ее боевые способности, но не память. Став охотником за головами, Алита начинает погоню не только за преступниками, но и за своими воспоминаниями.',CURRENT_DATE , CURRENT_DATE , CURRENT_DATE , 2019,'3weBFwmxR60','5.jpg'),
('Action','Мстители: Война бесконечности','Пока Мстители и их союзники продолжают защищать мир от различных опасностей, с которыми не смог бы справиться один супергерой, новая угроза возникает из космоса: Танос. Межгалактический тиран преследует цель собрать все шесть Камней Бесконечности — артефакты невероятной силы, с помощью которых можно менять реальность по своему желанию. Всё, с чем Мстители сталкивались ранее, вело к этому моменту — судьба Земли никогда ещё не была столь неопределённой.',CURRENT_DATE , CURRENT_DATE , CURRENT_DATE , 2018,'snDPok1ZOU','6.jpg'),
('Action','Стекло','В поисках встречи с обладающим фантастическими способностями Зверем, Дэвид Данн выходит на след Кевина Крамба. Однако в дело вмешивается Элайджа Прайс, который тщательно хранит секреты Данна и Крамба.',CURRENT_DATE , CURRENT_DATE , CURRENT_DATE , 2010,'cJ94Be-JSDo','7.jpg'),
('Action','Гарри Поттер и философский камень','Жизнь десятилетнего Гарри Поттера нельзя назвать сладкой: его родители умерли, едва ему исполнился год, а от дяди и тётки, взявших сироту на воспитание, достаются лишь тычки да подзатыльники. Но в одиннадцатый день рождения Гарри всё меняется. Странный гость, неожиданно появившийся на пороге, приносит письмо, из которого мальчик узнаёт, что на самом деле он волшебник и принят в Хогвартс — школу магии. А уже через пару недель Гарри будет мчаться в поезде Хогвартс-экспресс навстречу новой жизни, где его ждут невероятные приключения, верные друзья и самое главное — ключ к разгадке тайны смерти его родителей.',CURRENT_DATE , CURRENT_DATE , CURRENT_DATE , 2001,'q3urym79PQ4','8.jpg');
